package AlSong;

public class Quest2_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		int num1=82, num2=17;
		
		System.out.println("정수 82와 17의 합과 차를 구하는 프로그램");
		System.out.println(num1 + " + "+ num2 + " = " + (num1+num2));
		if(num1>num2) {
			System.out.println(num1 + " - "+ num2 + " = " + (num1-num2));
		}
		
		
	}

}
