package ch14;

public class MathTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Math.E =" +Math.E); //함수 e
		System.out.println("Math.PI =" +Math.PI);
		System.out.println("Math.abs(-9) =" +Math.abs(-9));
		System.out.println("Math.ceil(4.2) =" + Math.ceil(4.2)); // 올림
		System.out.println("Math.floor(4.9) =" +Math.floor(4.9));//버림
		System.out.println("Math.round(4.3) =" +Math.round(4.3)); //반올림
		System.out.println("Math.min(23,69) =" +Math.min(23, 67)); //최소값
		System.out.println("Math.max(34,67) =" +Math.max(34, 67)); //최댓값
		System.out.println("Math.exp(2) =" +Math.exp(2)); // 함수 e의 대한 거듭제곱값 함수 e는 맨위에.. 2.718... * 2.718...
		System.out.println("Math.log(2,3) =" +Math.pow(2, 3)); // 로그함수
		System.out.println("Math.sqrt(25) =" +Math.sqrt(25)); // 제곱근
		System.out.println("Math.sin(30) =" +Math.sin(30)); // sin
		System.out.println("Math.cos(30) =" +Math.cos(30)); // cos
		System.out.println("Math.tan(30) =" +Math.tan(30)); // tan
		
	}

}
